ew List<ColorModel>()            {                new ColorModel()                {                    ColorHex = Colors.White.ToString(),                    Offset = 0.2                }            };            return new List<ThemeModel>()            {                new ThemeModel()                {                    ThemeName = "自定义主题" + (ThemesCollection.Count(x=>x.Contains("自定义主题")) + 1),                    IsCustom = true,                    CategoryThemes = new List<GradientColorModel>()                    {                        new GradientColorModel()                        {                            CategoryName = "界面主题",                            ID=0,                            IsLinear = true,                            StartX = 0,                            StartY = 0,                            EndX = 1,                            EndY = 1,                            ColorModels = colorModels                        },                        new GradientColorModel()                        {                            CategoryName = "任务正在执行",                            ID=1,                            IsLinear = true,                            StartX = 0,                            StartY = 0,                            EndX = 1,                            EndY = 1,                            ColorModels = colorModels                        },                        new GradientColorModel()                        {                            CategoryName = "任务超时",                            ID=2,                            IsLinear = true,                            StartX = 0,                            StartY = 0,                            EndX = 1,                            EndY = 1,                            ColorModels = colorModels                        },                        new GradientColorModel()                        {                            CategoryName = "无任务计划",      