            //ThemesCollection.Add("一");
            var processPathInfo = Process.GetCurrentProcess().MainModule;
            var runtimePath = processPathInfo.FileName.Split(processPathInfo.ModuleName)[0];
            GlobalVariable.CustomThemePath = runtimePath + "CustomTheme.json";            var currentThemeName = GlobalVariable.JsonConfiguration["Themes:CurrentThemeName"];
            var theme = Themes.FirstOrDefault(x => x.ThemeName == currentThemeName);
            if (theme == null)            {                ExecuteLogger("【自定义主题】【加载配置异常】无可加载配置，已启用默认主题", true);                theme = Themes[0];                Themes.RemoveAll(x => x.ThemeName == currentThemeName);            }            LoadedThemeItemList(theme);        }        #endregion    }}